def divide_and_conquer(problem):
    # 基本情况
    if is_simple(problem):
        return solve_directly(problem)

    # 分解问题
    sub_problems = divide(problem)

    # 递归解决子问题
    sub_solutions = [divide_and_conquer(p) for p in sub_problems]

    # 合并结果
    return merge(sub_solutions)
